mapper.insert:
/* FileMapper.insertFile */
insert into file (
            file_id
          , file_group_id
          , file_url
          , file_name
          , file_path
          , created_by
          , created_date
          , modified_by
          , modified_date
) values (
           #{fileId}
         , #{fileGroupId}
         , #{fileUrl}
         , #{fileName}
         , #{filePath}
         , 'system'
         , CURRENT_TIMESTAMP
         , 'system'
         , CURRENT_TIMESTAMP
         )

mapper.select:
/* FileMapper.selectFileGroup */
select *
  from file_group
 where file_group_id = #{fileGroupId}

mapper.delete:
/* FileMapper.selectFileGroup */
delete
  from file_group
 where file_group_id = #{fileGroupId}

mapper.update:
/* FileMapper.updateFile */
update file
   set file_group_id = #{fileGroupId}
 where file_id = #{fileId}
