mapper.select:
/* TicketMapper.getTickets */ select all_list.ticket_id , all_list.ticket_code , all_list.member_name , all_list.trainer_product_name , all_list.ticket_start_end_date from (select a.ticket_id , a.ticket_code , d.member_name , e.trainer_product_name , CONCAT(DATE_FORMAT(STR_TO_DATE(a.ticket_start_date, '%Y%m%d'), '%Y.%m.%d'), ' ~ ', DATE_FORMAT(STR_TO_DATE(a.ticket_end_date, '%Y%m%d'), '%Y.%m.%d')) as ticket_start_end_date , ROW_NUMBER() OVER (ORDER BY a.ticket_start_date DESC) AS row_num from ticket a left join trainer b on a.trainer_id = b.trainer_id left join trainer_assignment c on a.member_id = c.member_id and b.trainer_id = c.trainer_id left join member d on a.member_id = d.member_id left join trainer_product e on a.trainer_product_id = e.trainer_product_id where a.trainer_id = #{trainerId} and a.ticket_delete_yn = 'N'
and ticket_code = #{ticketStatus}
) all_list where all_list.row_num >= case when #{currentPageNo} is null or #{currentPageNo}=0 or #{currentPageNo}=1 then 1 else (#{currentPageNo}-1)*10 end and all_list.row_num < #{currentPageNo}*10

/* TicketMapper.selectTicketByTicketId */ select a.ticket_id , a.ticket_start_date , a.ticket_end_date , a.ticket_code , a.ticket_suspend_start_date , a.ticket_suspend_end_date , a.ticket_use_cnt , a.original_ticket_id , a.ticket_relay_yn , b.trainer_product_id , b.trainer_id , b.trainer_product_name as ticket_name , b.trainer_product_price as ticket_price , b.trainer_product_count as ticket_total_cnt , c.member_id , c.member_name , c.member_phone , c.member_phone_end , c.member_birth , c.member_memo , d.center_id , d.center_name , d.center_tel , d.center_address , e.trainer_id , e.trainer_name , e.trainer_email , e.trainer_phone , LEFT(REGEXP_REPLACE(a.created_date, '[^0-9]', ''), 8) as created_date from ticket a left join trainer_product b on a.trainer_product_id = b.trainer_product_id left join member c on a.member_id = c.member_id left join center d on b.center_id = d.center_id left join trainer e on a.trainer_id = e.trainer_id where 1=1
and original_ticket_id = #{ticketId}
and ticket_id = #{ticketId}

/* TicketMapper.selectRefund */
select refund_cnt
     , refund_price
     , refund_date_time
  from refund
 where ticket_id = #{ticketId}

mapper.insert:
/* TicketMapper.insertTicket */
insert into ticket(
            created_by
          , created_date
          , member_id
          , ticket_end_date
          , ticket_id
          , ticket_start_date
          , trainer_id
          , trainer_product_id
          , ticket_code
          , original_ticket_id
          , ticket_use_cnt
          , ticket_relay_yn
  ) values (
            CURRENT_TIMESTAMP
          , #{trainerId}
          , #{memberId}
          , #{ticketEndDate}
          , #{ticketId}
          , #{ticketStartDate}
          , #{trainerId}
          , #{trainerProductId}
          , #{ticketCode}
          , #{originalTicketId}
          , #{ticketUseCnt}
          , #{ticketRelayYn}
  )

mapper.update:
/* TicketMapper.updateTicketForAssign */
update ticket
   set ticket_code = 'ASSIGN_TO'
     , modified_by = #{trainerId}
     , modified_date = CURRENT_TIMESTAMP
 where ticket_id = #{ticketId}

mapper.insert:
/* TicketMapper.insertMember */
insert into member(
            member_phone_end
          , member_birth
          , member_name
          , member_address
          , member_phone
          , center_id
          , create_by
          , create_date
          , member_join_path
          , member_memo
          , trainer_id
          , member_gender
          , member_id
   ) values (
            #{memberPhoneEnd}
          , #{AssignToNewMemberReqDto.memberBirth}
          , #{AssignToNewMemberReqDto.memberName}
          , #{AssignToNewMemberReqDto.memberAddress}
          , #{AssignToNewMemberReqDto.memberPhone}
          , #{AssignToNewMemberReqDto.centerId}
          , #{trainerId}
          , CURRENT_TIMESTAMP
          , #{AssignToNewMemberReqDto.memberJoinPath}
          , #{AssignToNewMemberReqDto.memberMemo}
          , #{AssignToNewMemberReqDto.trainerId}
          , #{AssignToNewMemberReqDto.memberGender}
          , #{memberId}
  )

/* TicketMapper.insertTrainerProduct */
insert into trainer_product(
            trainer_product_count
          , trainer_product_price
          , trainer_product_name
          , center_id
          , created_by
          , created_date
          , member_id
          , trainer_id
          , trainer_product_id
  ) values (
            #{trainerProductCount}
          , #{trainerProductPrice}
          , #{trainerProductName}
          , #{centerId}
          , #{trainerId}
          , CURRENT_TIMESTAMP
          , #{memberId}
          , #{trainerId}
          , #{trainerProductId}
  )

mapper.update:
/* TicketMapper.updateTicketForUseCnt */
update ticket
   set ticket_use_cnt = (ticket_use_cnt+1)
 where ticket_id = #{ticketId}
